import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Portfolio from './components/Portfolio';
import TradeSimulator from './components/TradeSimulator';
import RiskRewardChart from './components/RiskRewardChart';
import './App.css';

function App() {
  const [portfolio, setPortfolio] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  useEffect(() => {
    fetchPortfolio();
  }, [refreshKey]);

  const fetchPortfolio = async () => {
    try {
      const response = await axios.get('/portfolio');
      setPortfolio(response.data);
    } catch (error) {
      console.error('Error fetching portfolio:', error);
    }
  };

  const handleTradeComplete = () => {
    // Trigger refresh of all data
    setRefreshKey(prev => prev + 1);
  };

  return (
    <div className="App">
      <header className="header">
        <h1>Nordnet Trading Simulator</h1>
        <p className="subtitle">Practice trading without risking real money</p>
      </header>

      <div className="container">
        <div className="main-grid">
          <div className="left-column">
            <Portfolio portfolio={portfolio} />
            <TradeSimulator onTradeComplete={handleTradeComplete} />
          </div>

          <div className="right-column">
            <RiskRewardChart key={refreshKey} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
