import React, { useState } from 'react';
import axios from 'axios';
import './TradeSimulator.css';

function TradeSimulator({ onTradeComplete }) {
  const [symbol, setSymbol] = useState('');
  const [quantity, setQuantity] = useState('');
  const [loading, setLoading] = useState(false);

  const handleTrade = async (action) => {
    if (!symbol || !quantity) {
      alert('Please enter symbol and quantity');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post('/trade', {
        symbol: symbol.toUpperCase(),
        quantity: parseInt(quantity),
        action: action
      });

      if (response.data.error) {
        alert(response.data.error);
      } else {
        const trade = response.data.trade;
        const analysis = response.data.analysis;

        let message = `Trade executed successfully!\n\n`;
        message += `${action.toUpperCase()} ${trade.quantity} shares of ${trade.symbol}\n`;
        message += `Price: $${trade.price.toFixed(2)}\n`;
        message += `Total: $${trade.total_value.toFixed(2)}`;

        if (analysis && analysis.risk_warning) {
          message += `\n\n‚ö†Ô∏è ${analysis.risk_warning}`;
        }

        alert(message);
        setSymbol('');
        setQuantity('');

        if (onTradeComplete) {
          onTradeComplete();
        }
      }
    } catch (error) {
      console.error('Error executing trade:', error);
      alert('Error executing trade');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="trade-simulator-card">
      <h2>Execute Trade</h2>
      <div className="trade-form">
        <div className="form-group">
          <label htmlFor="symbol">Stock Symbol</label>
          <input
            id="symbol"
            type="text"
            placeholder="e.g., AAPL"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value)}
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label htmlFor="quantity">Quantity</label>
          <input
            id="quantity"
            type="number"
            placeholder="Number of shares"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            min="1"
            disabled={loading}
          />
        </div>

        <div className="trade-buttons">
          <button 
            className="buy-btn" 
            onClick={() => handleTrade('buy')}
            disabled={loading}
          >
            {loading ? 'Processing...' : 'Buy'}
          </button>
          <button 
            className="sell-btn" 
            onClick={() => handleTrade('sell')}
            disabled={loading}
          >
            {loading ? 'Processing...' : 'Sell'}
          </button>
        </div>
      </div>

      <div className="trade-info">
        <p>üí° <strong>Tip:</strong> Stock prices are dynamically generated based on the symbol.</p>
      </div>
    </div>
  );
}

export default TradeSimulator;
