import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RiskRewardChart.css';

function RiskRewardChart() {
  const [riskMetrics, setRiskMetrics] = useState(null);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [riskResponse, historyResponse] = await Promise.all([
        axios.get('/risk'),
        axios.get('/history')
      ]);

      setRiskMetrics(riskResponse.data);
      setHistory(historyResponse.data.trades || []);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const getRiskLevelColor = (level) => {
    switch (level) {
      case 'low': return '#4caf50';
      case 'medium': return '#ff9800';
      case 'high': return '#f44336';
      default: return '#999';
    }
  };

  return (
    <div className="risk-reward-section">
      <div className="risk-metrics-card">
        <h2>Risk Metrics</h2>
        {!riskMetrics ? (
          <p>Loading risk metrics...</p>
        ) : (
          <div className="metrics-grid">
            <div className="metric-item">
              <span className="metric-label">Total Portfolio Value</span>
              <span className="metric-value">
                ${riskMetrics.total_value?.toFixed(2) || '0.00'}
              </span>
            </div>

            <div className="metric-item">
              <span className="metric-label">Risk Level</span>
              <span 
                className="metric-value risk-badge"
                style={{ backgroundColor: getRiskLevelColor(riskMetrics.risk_level) }}
              >
                {riskMetrics.risk_level?.toUpperCase() || 'N/A'}
              </span>
            </div>

            <div className="metric-item">
              <span className="metric-label">Volatility</span>
              <span className="metric-value">
                {(riskMetrics.volatility * 100)?.toFixed(1) || '0'}%
              </span>
            </div>

            <div className="metric-item">
              <span className="metric-label">Diversification Score</span>
              <span className="metric-value">
                {riskMetrics.diversification_score?.toFixed(0) || '0'}/100
              </span>
            </div>

            {riskMetrics.concentration !== undefined && (
              <div className="metric-item">
                <span className="metric-label">Largest Position</span>
                <span className="metric-value">
                  {(riskMetrics.concentration * 100)?.toFixed(1)}%
                </span>
              </div>
            )}
          </div>
        )}
      </div>

      <div className="trade-history-card">
        <h2>Trade History</h2>
        {history.length === 0 ? (
          <p className="no-data">No trades yet</p>
        ) : (
          <div className="history-table-container">
            <table className="history-table">
              <thead>
                <tr>
                  <th>Time</th>
                  <th>Action</th>
                  <th>Symbol</th>
                  <th>Quantity</th>
                  <th>Price</th>
                  <th>Total</th>
                </tr>
              </thead>
              <tbody>
                {history.slice().reverse().map((trade, idx) => (
                  <tr key={idx}>
                    <td>{new Date(trade.timestamp).toLocaleString()}</td>
                    <td>
                      <span className={`action-badge ${trade.action}`}>
                        {trade.action.toUpperCase()}
                      </span>
                    </td>
                    <td className="symbol">{trade.symbol}</td>
                    <td>{trade.quantity}</td>
                    <td>${trade.price?.toFixed(2)}</td>
                    <td>${trade.total_value?.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default RiskRewardChart;
